import React, { useEffect } from "react";
import { Route, Routes, Navigate } from "react-router-dom";
import Footer from "./Footer";
import Header from "./Header";
import Gallery from "./Gallery";
import Basket from "./Basket";
import useStore from "../store";

const App = () => {
  const store = useStore();
  const { items, addItem, deleteItem } = store;

  useEffect(() => {
    // Загрузка данных из localStorage при монтировании компонента
    const storedItems = localStorage.getItem("items");
    if (storedItems) {
      store.setItems(JSON.parse(storedItems));
    }
  }, [store]);

  useEffect(() => {
    // Обновление localStorage при изменении items
    localStorage.setItem("items", JSON.stringify(items));
  }, [items]);

  function onAddItemBusket(item) {
    addItem(item);
  }

  function onDecreaseItemBusket(item) {
    deleteItem(item);
  }

  return (
      <div className="page">
        <Routes>
          <Route
            path="/"
              element={
                <main className="main">
                  <Header
                      basketCount={items.length}
                  />
                  <section className="gallery">
                    <Gallery
                        title="Наушники"
                        data={headphones}
                        onCardClick={onAddItemBusket}
                    />
                    <Gallery
                        title="Беспроводные наушники"
                        data={headphonesWireless}
                        onCardClick={onAddItemBusket}
                    />
                  </section>

                  <Footer/>
                </main>
              }
          />
          <Route
            path="/basket"
            element={
              <main>
                <Header busketCount={items.length}/>
                <Basket 
                  onCardClick={onAddItemBusket}
                  onDecreaseItem={onDecreaseItemBusket}
                />
                <Footer/>
              </main>
            }
          />


          <Route path="*" element={<Navigate to="/" replace/>}/>
        </Routes>
      </div>
  );
}

export default App;
