import {create} from "zustand";

const useStore = create((set) => ({
  items: JSON.parse(localStorage.getItem("items")) || [],
  addItem: (item) =>
    set((state) => ({
      items: [...state.items, { ...item }],
    })),
  deleteItem: (id) =>
    set((state) => ({
      items: state.items.filter((item) => item.id !== id)
    })),
  decreaseItem: (id) =>
    set((state) => {
